#-*- coding: utf-8 -*-


class Solution(object):
    def findSubstring(self, s, words):
        """
        :type s: str
        :type words: List[str]
        :rtype: List[int]
        """
        if not words:
            return []

        total_len = len(words) * len(words[0])
        if total_len > len(s):
            return []

        res = []
        for i in range(len(s) - total_len + 1):
            # if self.inner_find(i, s, words, []):
            if self.is_valid(i, s, words):
                res.append(i)

        return res

    def is_valid(self, index, s, words):
        word_split = []
        length = len(words[0])
        for k in range(len(words)):
            word_split.append(s[index + k * length:index + (k + 1) * length])
        for w in words:
            if w in word_split:
                word_split.remove(w)
            else:
                return False
        return True

    def inner_find(self, index, s, words, has_word_index):
        if len(has_word_index) == len(words):
            return True

        for i, word in enumerate(words):
            if i not in has_word_index:
                if s[index:len(word) + index] == word:
                    has_word_index.append(i)
                    index = len(word) + index
                    return self.inner_find(index, s, words, has_word_index)

        return False


print(Solution().findSubstring("barfoofoobarthefoobarman",
                               ["bar", "foo", "the"]))
